import React, {useEffect, useState} from 'react';

function TODOList(){
    const [theArray, setTheArray] = useState([]);
    const [inputValue, setInputValue] = React.useState(null);

    const addEntryClick = () => {
        setTheArray([...theArray, `-> ${inputValue}`]);
    };

    const checkTasks = () => {
        if(theArray.length){
            return(
                <div>
                    {theArray.map(entry =>
                        <p>{entry}</p>
                    )}
                </div>
            )
        }
        else{
            return(<p>Nothing to do!</p>)
        }

    }

    return (
        <div>
            <div className="container">
                <div className="row" style={{marginTop:"5%"}}>
                    <div className="container">
                        <div className="row">
                            <div className="col-sm-6">
                                <input type="text" className="form-control input-normal" placeholder="insert new task" value={inputValue} onChange={(event) => setInputValue(event.target.value)} />
                            </div>
                            <div className="col-sm-6">
                                <input type="button" onClick={addEntryClick} value="Add" />
                            </div>
                        </div>
                    </div> 
                </div>
            </div>

            {checkTasks()}
        </div>
    );
