import React, {useEffect} from 'react';

function Drivers(){

    const [driversList, setDriversList] = React.useState([]);
    const [pageSize, setPageSize] = React.useState(20);
    const [page, setPage] = React.useState(1);
    const [totalDrivers, setTotalDrivers] = React.useState(0);


    const fetchState = async () => {
        const fetchItem = await fetch('http://192.168.160.58/Formula1/api/Drivers?page=' + page + '&pagesize=' + pageSize);
        const item = await fetchItem.json();
        setDriversList(item.List);
    };

    const getTotalDrivers = async () => {
        const fetchItem = await fetch('http://192.168.160.58/Formula1/api/Drivers');
        const item = await fetchItem.json();
        setTotalDrivers(item.List.length);
    };

    const retrieveDrivers = () => {
        return (
            <div>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th scope="col">Name</th>
                            <th scope="col">Nationality</th>
                        </tr>
                    </thead>
                    <tbody>
                        {driversList.map(driver =>
                            <tr key={driver.id}>
                                <td>{driver.Name}</td>
                                <td>{driver.Nationality}</td>
                            </tr>
                        )}  
                    </tbody>
                </table>
            </div>
        )
    }

    const changePageForward = () => {
        setPage(page+1);
        fetchState();
    }

    const changePageBackward = () => {
        if(page > 1){
            setPage(page-1);
            fetchState();
        }
    }

    const goToPage = (i) => {
        setPage(i);
        fetchState();
    }

    const getPaginationNumbers = () => {
        const totalDrivers = driversList.length;
        const totalPages = totalDrivers / pageSize;
        console.log("total pages "+pageSize)
        for (let i = 1; i<totalPages; i++){
            return(
                <li className="page-item"><a className="page-link" onClick={goToPage(i)}>{i}</a></li>
            )

        }
    }

    useEffect(() => {
        fetchState();
    },[])

    return(
        <div>
            <h1>Get to know the drivers</h1>
            {retrieveDrivers()}
            <nav aria-label="Page navigation example">
                <ul className="pagination">
                    <li className="page-item"><a className="page-link" onClick={changePageBackward}>Previous</a></li>
                    {getPaginationNumbers()}
                    <li className="page-item"><a className="page-link" onClick={changePageForward}>Next</a></li>
                </ul>
            </nav>
        </div>

    )
}

export default Drivers;
