import React from 'react';

function TODOList(){
    const [tasks, setTasks] = React.useState([]);
    const [newTask, setNewTask] = React.useState(null);
    const checkTasks = () => {
        console.log(tasks.length)
        if (tasks.length === 0){
            return(
                <p style={{marginTop:"5%"}}>Nothing to do!</p>
            )
        }
        else{
            return(
                <div className="users">
                    {tasks.map((task) => (
                        <p key={task} style={{marginTop:"5%"}} >{task}</p>
                    ))}
                </div>
            )
        }
    }

    const addTask = () => {

        let clone = [...tasks]
        let clone2 = []
        for (let c of clone){
            clone2.push(c);
        }
        setTasks(clone2);
    }


    return(
        <div>
            <h1>ToDo List</h1>

            <div className="container">
                <form role="form">
                    <div className="row" style={{marginTop:"5%"}}>
                        <div className="container">
                            <div className="row">
                                <div className="col-sm-6">
                                    <input type="text" className="form-control input-normal" placeholder="insert new task" value={newTask} onChange={e => setNewTask(e.target.value)} />
                                </div>
                                <div className="col-sm-6">
                                <button type="submit" className="btn btn-default" onClick={addTask}>Submit</button>
                                </div>
                            </div>
                        </div>

                    </div>
                </form>
            </div>

            {checkTasks()}

        </div>  
    )
}

export default TODOList;
