import React from 'react';

function TODOList(){
    const [tasks, setTasks] = React.useState([]);
    const [newTask, setNewTask] = React.useState(" ");
    const checkTasks = () => {
        if (tasks.length === 0){
            return(
                <p style={{marginTop:"5%"}}>Nothing to do!</p>
            )
        }
        else{
            return(
                <div className="users">
                    {tasks.map((task) => (
                        <p key={task} style={{marginTop:"5%"}} >{task}</p>
                    ))}
                </div>
            )
        }
    }


    return(
        <div>
            <h1>ToDo List</h1>

            <div class="container">
                <form role="form">
                    <div class="row" style={{marginTop:"5%"}}>
                        <div class="container">
                            <div class="row">
                                <div class="col-sm-6">
                                    <input type="text" class="form-control input-normal" placeholder="insert new task" value={{newTask}}/>
                                </div>
                                <div class="col-sm-6">
                                <button type="submit" class="btn btn-default" onClick={() => setTasks(tasks => [...tasks, newTask])}>Submit</button>
                                </div>
                            </div>
                        </div>

                    </div>
                </form>
            </div>

            {checkTasks()}

        </div>  
    )
}

export default TODOList;
