import React, {useEffect, useState} from 'react';

function TODOList(){
    const [tasks, setTasks] = useState([]);
    const [newTask, setNewTask] = React.useState(null);

    const addNewTask = () => {
        setTasks([...tasks, newTask]);
    };

    const checkTasks = () => {
        if (tasks.length){
            return(
                <div className="container">
                    <div className="row" style={{marginTop:"5%"}}>
                        <div className="container">
                            <div className="row">
                                <div className="col-sm-6">
                                    {tasks.map(entry =>
                                        <div>
                                        <p>{entry}</p>
                                        <input type="button"value="Remove" />
                                        </div>
                                    )}
                                </div>
                                <div className="col-sm-6">

                                </div>
                            </div>
                        </div> 
                    </div>
                </div>
            )
        }
    }

    {/* 
    const checkTasks = () => {
        if(tasks.length){
            return( 
                <div>
                    {tasks.map(entry =>
                        <p>{entry}</p>
                    )}
                </div>
            )
        }
        else{
            return(<p>Nothing to do!</p>)
        }
    }
    */}

    return (
        <div>
            <h1>Your TODO list</h1>
            <div className="container">
                <div className="row" style={{marginTop:"5%"}}>
                    <div className="container">
                        <div className="row">
                            <div className="col-sm-6">
                                <input type="text" className="form-control input-normal" placeholder="insert new task" value={newTask} onChange={(event) => setNewTask(event.target.value)} />
                            </div>
                            <div className="col-sm-6">
                                <input type="button" onClick={addNewTask} value="Add" />
                            </div>
                        </div>
                    </div> 
                </div>
            </div>
            {checkTasks()}
        </div>
    );
}

export default TODOList;
