import React from 'react';

function TODOList(){
    const [tasks, setTasks] = React.useState([]);
    const [newTask, setNewTask] = React.useState("");
    const checkTasks = () => {
        console.log(tasks.length)
        if (tasks.length === 0){
            return(
                <p style={{marginTop:"5%"}}>Nothing to do!</p>
            )
        }
        if (tasks.length != 0){
            return(
                <div className="users">
                    {tasks.map((task) => (
                        <p key={task} style={{marginTop:"5%"}} >{task}</p>
                    ))}
                </div>
            )
        }
    }



    return(
        <div>
            <h1>ToDo List</h1>

            <div className="container">
                <form role="form">
                    <div className="row" style={{marginTop:"5%"}}>
                        <div className="container">
                            <div className="row">
                                <div className="col-sm-6">
                                    <input type="text" className="form-control input-normal" placeholder="insert new task" value={newTask} onChange={e => setNewTask(e.target.value)} />
                                </div>
                                <div className="col-sm-6">
                                <button type="submit" className="btn btn-default" onClick={() => setTasks(tasks => [...tasks, newTask])}>Submit</button>
                                </div>
                            </div>
                        </div>

                    </div>
                </form>
            </div>

            {checkTasks()}

        </div>  
    )
}

export default TODOList;
