import React, {useEffect} from 'react';

function TODOList(){
    const [tasks, setTasks] = React.useState([]);
    const [inputValue, setInputValue] = React.useState(null);
    const checkTasks = () => {
        console.log(tasks.length)
            return(
                <div className="tasks">
                    {tasks.map((task) => (
                        <p style={{marginTop:"5%"}} >{task.value}</p>
                    ))}
                </div>
            )

    }

    const addTask = () => {
        const newTask = {
            value: inputValue
        }
        const newTasks = [...tasks, newTask];

        setTasks(newTasks);
        setInputValue('');

    }


    return(
        <div>
            <h1>ToDo List</h1>

            <div className="container">
                <form role="form">
                    <div className="row" style={{marginTop:"5%"}}>
                        <div className="container">
                            <div className="row">
                                <div className="col-sm-6">
                                    <input type="text" className="form-control input-normal" placeholder="insert new task" value={inputValue} onChange={(event) => setInputValue(event.target.value)} />
                                </div>
                                <div className="col-sm-6">
                                <button type="submit" className="btn btn-default" onClick={() => addTask()}>Submit</button>
                                </div>
                            </div>
                        </div>

                    </div>
                </form>
            </div>

            {checkTasks()}

        </div>  
    )
}

export default TODOList;
