import React from 'react';

function TODOList(){
    const nullTask = ["Nothing to do!"];
    const [tasks, setTasks] = React.useState(["hehehe"]);
    const checkTasks = () => {
        if (tasks.length === 0){
            return(
                <p>Nothing to do!</p>
            )
        }
        else{
            {tasks.map((task) =>{
                return <p style={{marginTop:"5%"}} key={task}>{task}</p>;
            })}
        }
    }
    return(
        <div>
            <h1>ToDo List</h1>
            {checkTasks()}

            <div class="container">
                <form role="form">
                    <div class="row" style={{marginTop:"5%"}}>
                        <div class="container">
                            <div class="row">
                                <div class="col-sm-6">
                                    <input type="text" class="form-control input-normal" placeholder="insert task" />
                                </div>
                                <div class="col-sm-6">
                                <button type="submit" class="btn btn-default">Submit</button>
                                </div>
                            </div>
                        </div>

                    </div>
                </form>
            </div>
        </div>  
    )
}

export default TODOList;
